INSERT INTO `sys_code_config` VALUES ('15b85d14e17b4b048c708d031e39f5b0', '全部信访', 'um.visits.all.query', '', null, null, 'select RESOURCEID,visitnum,xm,date_format(XFRQ,\'yyyy-MM-dd\') as xfrq,processingunit,handletype,prostatus,xfxs,sjh,visitlevel,xfrs,zjhm,txdz,djr,lf_contclass,sourcetype,receptionleader,xfmddm,dc_timelimit,xfjzt,isapplysupervision,cfxfbz,lamp\r\nFrom Visit_Contents t  where 1=1\r\n<#if resourceId??>  AND ( t.resourceId= ${resourceId})</#if> \r\n<#if visitnum??>  AND ( t.visitnum= ${visitnum})</#if> \r\n<#if visitnum2??>  AND ( t.visitnum= ${visitnum2})</#if>\r\n<#if xm??> AND (t.xm like \'%${urms_original(xm)}%\')</#if>\r\n<#if xm2??> AND (t.xm like \'%${urms_original(xm2)}%\')</#if>\r\n<#if processingunit2??>  AND (t.processingunit like \'%${urms_original(processingunit2)}%\')</#if> \r\n<#if zjhm??>  AND ( t.zjhm= ${zjhm})</#if>\r\n<#if zjhm2??>  AND ( t.zjhm=${zjhm2})</#if>\r\n<#if startTime??>  AND ( date_format(t.xfrq,\'yyyy-mm-dd\') >= \'${urms_native(startTime)}\')</#if>\r\n<#if endTime??>  AND ( date_format(t.xfrq,\'yyyy-mm-dd\') <= \'${urms_native(endTime)}\')</#if>\r\n<#if startTime2??>  AND ( date_format(t.xfrq,\'yyyy-mm-dd\') >= \'${urms_native(startTime2)}\')</#if>\r\n<#if endTime2??>  AND ( date_format(t.xfrq,\'yyyy-mm-dd\') <= \'${urms_native(endTime2)}\')</#if>\r\n<#if problemregname??>  AND (t.problemregname like \'%${urms_original(problemregname)}%\')</#if>\r\n<#if sjh??>  AND (t.sjh like \'%${urms_original(sjh)}%\')</#if>\r\n<#if tsnr??>  AND (t.tsnr like \'%${urms_original(tsnr)}%\')</#if>\r\n<#if gkxx??>  AND (t.gkxx like \'%${urms_original(gkxx)}%\')</#if>\r\n<#if wfstatus??>  AND ( t.wfstatus= ${wfstatus})</#if> \r\n<#if xfxs??>  AND ( t.xfxs= ${xfxs})</#if> \r\n<#if xfmddm??> AND (t.xfmddm=${xfmddm})</#if>\r\n<#if prostatus??> AND (t.prostatus=${prostatus})</#if>\r\n<#if handletype??> AND (t.handletype=${handletype})</#if>\r\n<#if visitlevel??> AND (t.visitlevel=${visitlevel})</#if>\r\n<#if djr??> AND (t.djr=${djr})</#if>\r\n<#if xfjzt??> AND (t.xfjzt=${xfjzt})</#if>\r\n<#if isapplysupervision??> AND (t.isapplysupervision=${isapplysupervision})</#if>\r\n<#if cfxfbz??> AND (t.cfxfbz=${cfxfbz})</#if>\r\n<#if lamp??> AND (t.lamp=${lamp})</#if>\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\norder by a.CREATETIME desc\r\n', '2015-04-14 14:02:28', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-04-14 14:02:28', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('178acfa1c5d54e6c8a02a1791e54f33f', '下级复查复核', 'um.visit.review.query.lower', '', null, null, 'SELECT a.resourceid,a.VISITNUM,a.BUSINESSNUM,\r\n       a.FC_APPLYTYPE,\r\n        a.XM, date_format(a.CREATETIME,\'yyyy-mm-dd hh24:mi:ss\') AS CREATETIME,\r\n				a.PROSTATUS,a.HANDLETYPE,a.TSNR,\r\n        a.NRFLDM,a.XFRS,a.SJH,a.ZJHM,a.ZZ,a.DJR,a.PROCESSINGUNIT,a.CONTENTCLASSNAME,\r\n        date_format(a.xfrq,\'yyyy-mm-dd\') AS xfrq,\r\n        lamp,lamptext\r\nFROM\r\n  VISIT_CONTENTS a\r\nWHERE a.XFXS = \'902\' and a.visitlevel=2\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n<#if businessNum2??>  AND ( a.businessNum like \'%${urms_native(businessNum2)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if fc_applytype2??>  AND ( TRIM(a.fc_applytype) = \'${urms_native(fc_applytype2)}\')</#if>\r\n<#if businessNum??>  AND ( a.businessNum like \'%${urms_native(businessNum)}%\')</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if visitnum2??>  AND ( a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if fc_applytype??>  AND ( TRIM(a.fc_applytype) = \'${urms_native(fc_applytype)}\')</#if>\r\n<#if xfrq??>  AND ( date_format(a.xfrq,\'yyyy-mm-dd\') = \'${urms_native(xfrq)}\')</#if>\r\n<#if handletype??>  AND ( TRIM(a.handletype) = \'${urms_native(handletype)}\')</#if>\r\n<#if prostatus??>  AND ( TRIM(a.prostatus) = \'${urms_native(prostatus)}\')</#if>\r\n<#if publictype??>  AND ( TRIM(a.publictype) = \'${urms_native(publictype)}\')</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\nORDER BY a.CREATETIME DESC', '2015-07-20 18:45:57', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-20 18:45:57', '管理员', '4761EE3EA19CC50BE4