INSERT INTO `sys_code_config` VALUES ('22dfe2954f99481f9c3e4624c6c9588a', '上级人民建议征集', 'um.visits.adviceUplevel.query', '', null, null, ' select resourceid,businessnum,gkxx,visitnum,xm,DATE_FORMAT(a.XFRQ,\'%y-%m-%d\') as xfrq,PROCESSINGUNIT,curActivityName,curOperator,tsnr,(case when PROSTATUS=\'0\' then \'未处理\' when PROSTATUS=\'1\' then \'审核中\' when PROSTATUS=\'2\' then \'处理中\' when PROSTATUS=\'3\' then \'退回申请\' when PROSTATUS=\'4\' then \'审核中\' when PROSTATUS=\'5\' then \'申请办结\' when PROSTATUS=\'6\' then \'已办结\' end)as prostatus, xfrs, zz, CONTENTCLASSNAME, (case when xfmddm=\'01\' then \'意见建议\' when xfmddm=\'02\' then \'申诉\' when xfmddm=\'03\' then \'求决\' when xfmddm=\'04\' then \'揭发控告\' when xfmddm=\'99\' then \'其他\' end)as xfmddm, netnumber,callnumber, reviewnumber, supervisionnumber,PROBLEMREGNAME, letternumber, receptionnumber, lamp,lamptime , rownum from visit_contents   a\r\nwhere a.xfxs=\'700\' and a.visitlevel=\'0\'\r\n            and a.sourceType=2\r\n<#if selectType?index_of(\'MYD\')!=-1>\r\n   and a.issatisfied= 1\r\n<#elseif selectType?index_of(\'NORMAL\')!=-1>\r\n   and a.oldHandletype=\'010000\' and a.issatisfied != 1\r\n<#elseif selectType?index_of(\'YSB\')!=-1>\r\n   and a.isreport=1\r\n<#elseif selectType?index_of(\'THGJ\')!=-1>\r\n   and a.isreport=-1\r\n<#elseif selectType?index_of(\'PC\')!=-1>\r\n   and a.oldHandletype not in(\'010000\',\'020000\',\'030000\')\r\n</#if>\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if djr??>  AND ( a.djr like \'%${urms_native(djr)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if startTime??>  AND ( DATE_FORMAT(a.xfrq,\'%y-%m-%d\') >= \'${urms_native(startTime)}\')</#if>  \r\n<#if endTime??>  AND ( DATE_FORMAT(a.xfrq,\'%y-%m-%d\') <= \'${urms_native(endTime)}\')</#if>  \r\n<#if createtime??>  AND ( DATE_FORMAT(a.createtime,\'%y-%m-%d hh24:mi:ss\') = \'${urms_native(createtime)}\')</#if>  \r\norder by a.CREATETIME desc', '2015-07-10 14:44:49', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-10 14:44:49', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '上级人民建议征集', '0');
