INSERT INTO `sys_code_config` VALUES ('27d08bd9471549f1bf1fe4967db0d6bf', '重复信访', 'um.visit.visitcontents.query.repeat', '', null, null, 'SELECT v.RESOURCEID,v.XFXS,v.VISITNUM,v.BUSINESSNUM,\r\n        v.XM,v.ZJHM,DATE_FORMAT(v.XFRQ,\'%Y-%m-%d\') AS XFRQ,\r\n				v.PROSTATUS,v.HANDLETYPE,v.GKXX,\r\n        v.CONTENTCLASSNAME,v.DJR,v.ZZ,v.PROCESSINGUNIT\r\nFROM\r\n  VISIT_CONTENTS v\r\nWHERE\r\n1=1\r\n<#if xm??>  \r\n   and v.resourceid in (\r\n		select visit_id from VISIT_USERS u where  \r\n			u.xm = ${xm}\r\n			<#if zz??> AND u.zz = ${zz}</#if>\r\n			<#if sjh??>  AND u.sjh = ${sjh}</#if>\r\n			<#if zjhm??> OR u.zjhm = ${zjhm}</#if>\r\n	)\r\n</#if>\r\n\r\n<#if resourceid??>  AND ( v.resourceid!= \'${urms_native(resourceid)}\')</#if>\r\n<#if xfxs??>  AND ( v.xfxs= \'${urms_native(xfxs)}\')</#if>\r\n ORDER BY IF(v.HANDLETYPE is NULL, \'999999\', v.HANDLETYPE) ASC, v.CREATETIME DESC\r\n', '2015-05-23 10:14:19', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-05-23 10:14:19', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '判重列表', '0');
