INSERT INTO `sys_code_config` VALUES ('292b684daa7a408e959659385871e235', '历史信访', 'um.visits.allvisit.history.query', '', null, null, 'select a.resourceid,a.BUSINESSNUM,a.visitnum,a.xfxs,a.XM,date_format(a.XFRQ,\'yyyy-MM-dd\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,prostatus,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.CONTENTCLASSNAME,a.XFMDDM,a.SXR,a.XBJZSJ, \r\n lamp\r\nfrom VISIT_CONTENTS a where  (SOURCETYPE=3 or SOURCETYPE=4 or SOURCETYPE=6)\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND ( a.processinguniidlike \'%${urms_native(processinguniid)}%\')</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if xfxs??>  AND ( a.xfxs=${xfxs})</#if>\r\n<#if visitLevel??>  AND ( a.visitLevel=${visitLevel})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if xfxs2??>  AND ( a.xfxs=${xfxs2})</#if>\r\n<#if visitLevel2??>  AND ( a.visitLevel=${visitLevel2})</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime2})</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'yyyy-mm-dd hh24:mi:ss\') = \'${urms_native(createtime)}\')</#if>  \r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\nORDER BY a.CREATETIME DESC', '2015-07-13 18:50:23', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-13 18:50:23', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '全部信访', '0');
INSERT INTO `sys_code_config` VALUES ('29d79f35baed403ebc2071b6ffa3ffc5', '上报复核信息', 'um.common.reportrecord.update.visit.third', '', null, null, 'select * from visit_contents t, visit_reply r \r\nwhere t.fc_applytype = \'third\' and t.visitnum = r.xfjbh and t.resourceid=r.visit_id\r\nand t.fc_check_flag<2\r\nand t.isreport=0\r\nand r.sdfs<3\r\nand (r.xfryj<2 or r.xfryj=9)\r\nand r.qsbz<2\r\nand t.resourceId=${resourceId}', '2015-06-02 10:46:49', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-02 10:46:49', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '国家对接', '0');
INSERT INTO `sys_code_config` VALUES ('2c38e12917604ee3bd199ae2952fc3f0', '本级立案申请', 'um.visit.apply.query.all', '', null, null, 'SELECT a.RESOURCEID,a.BUSINESSNUM,a.VISITNUM,a.CURACTIVITYNAME,\r\n		a.DJBM,date_format(a.CREATETIME,\'%Y-%m-%d\')  AS CREATETIME,\r\n                date_format(a.XFRQ,\'%Y-%m-%d\') AS XFRQ,cxm,		a.DC_SUPERVISESOURCE,a.XM,a.XFRS,a.ZJHM,a.DC_HOSTUNITNAME,a.DC_CCUNITNAME,a.DJR,a.TSNR,\r\n                 lamp,lamptext\r\nFROM\r\n  VISIT_CONTENTS a \r\nWHERE\r\n  a.XFXS = \'901\' AND a.dc_isapply = 1 \r\n\r\n<#if visitLevel??>  AND ( visitLevel = \'${urms_native(visitLevel)}\')</#if>\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if applysupervisionstate??>  AND ( TRIM(a.APPLYSUPERVISIONSTATE) = \'${urms_native(applysupervisionstate)}\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if visitnum??>  AND ( a.VISITNUM like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if dc_supervisesource??>  AND ( a.dc_supervisesource = \'${urms_native(dc_supervisesource)}\')</#if>\r\n<#if createTimeStart??>  and date_format(a.CREATETIME, \'%Y-%m-%d\') >= \'${urms_native(createTimeStart)}\'</#if>\r\n<#if createTimeEnd??>  and date_format(a.CREATETIME, \'%Y-%m-%d\') <= \'${urms_native(createTimeEnd)}\'</#if>\r\n<#if djbm??>  AND ( a.djbm like \'%${urms_native(djbm)}%\')</#if>\r\n<#if djr??>  AND ( a.djr like \'%${urms_native(djr)}%\')</#if>\r\n<#if dc_checkusername??>  AND ( a.dc_checkusername like \'%${urms_native(dc_checkusername)}%\')</#if>\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if xfrqStart??>  and date_format(a.xfrq, \'%Y-%m-%d\') >= \'${urms_native(xfrqStart)}\'</#if>\r\n<#if xfrqEnd??>  and date_format(a.xfrq, \'%Y-%m-%d\') <= \'${urms_native(xfrqEnd)}\'</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if visitnum2??>  AND ( a.VISITNUM like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if dc_supervisesource2??>  AND ( a.dc_supervisesource = \'${urms_native(dc_supervisesource2)}\')</#if>\r\n<#if createTimeStart2??>  and date_format(a.CREATETIME, \'%Y-%m-%d\') >= \'${urms_native(createTimeStart2)}\'</#if>\r\n<#if createTimeEnd2??>  and date_format(a.CREATETIME, \'%Y-%m-%d\') <= \'${urms_native(createTimeEnd2)}\'</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\nORDER BY a.CREATETIME DESC', '2015-05-08 13:49:19', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-05-08 13:49:19', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '立案申请', '0');
INSERT INTO `sys_code_config` VALUES ('2f085163e8724e93b99af1d06a96ea4a', '退回申请原因列表', 'um.visit.returnapply.query', '', null, null, 'select a.resourceId as \'RESOURCEID\',a.visit_id as \'VISIT_ID\',a.visit_num as \'VISIT_NUM\',a.applyunit as \'APPLYUNIT\',a.returnreason as \'RETURNRESON\',(CASE WHEN isthrough=0 THEN \'否\' WHEN isthrough=1 THEN \'是\' WHEN isthrough=2 THEN \'待审\' END) as \'PROSTATUS\',DATE_FORMAT(a.handletime,\'%Y-%m-%d\') as \'HANDLETIME\',DATE_FORMAT(a.oldhandletime,\'%Y-%m-%d\') as \'OLDHANDLETIME\',a.receiveuser as \'RECEIVEUSER\',a.isthrough as \'ISTHROUGH\',a.isattachment as \'ISATTACHMENT\',a.RECEIVEUSERID as \'RECEIVEUSERID\' \r\nfrom VISIT_RETURNAPPLYLOG a where 1=1 \r\n<#if visitId??>  AND (a.visit_id= ${visitId})</#if>\r\n<#if RECEIVEUSERID??> <#if receiveuser??>  AND ( a.RECEIVEUSERID= ${RECEIVEUSERID} OR a.receiveuser= ${receiveuser} ) </#if> </#if>\r\n<#if isthrough??>  AND (a.isthrough= ${isthrough})</#if>\r\n order by CREATETIME desc', '2015-06-02 16:34:10', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-02 16:34:10', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('2f453e6d64ef464aab51ed2cb2196bef', '判重列表', 'um.visit.visitcontents.query.all', '', null, null, 'SELECT v.RESOURCEID,v.XFXS,v.VISITNUM,v.BUSINESSNUM,\r\n        v.XM,v.ZJHM,date_format(v.XFRQ,\'%Y-%m-%d\') AS XFRQ,\r\n				v.PROSTATUS,v.HANDLETYPE,v.GKXX,\r\n        v.CONTENTCLASSNAME,v.DJR,v.ZZ,v.PROCESSINGUNIT\r\nFROM\r\n  VISIT_CONTENTS v\r\nWHERE\r\n1=1\r\n<#if xfxs??>  AND ( v.xfxs = \'${urms_native(xfxs)}\')</#if>\r\n<#if xm??>  AND ( v.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if zjhm??>  AND  ( v.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if gkxx??>  AND  ( v.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if handletype??>  AND  ( v.handletype =\'${urms_native(handletype)}\')</#if>\r\n<#if zz??>  AND ( v.zz = \'${urms_native(zz)}\')</#if>\r\n<#if sjh??>  AND ( v.sjh = \'${urms_native(sjh)}\')</#if>\r\n<#if resourceId??>  AND ( v.resourceId != \'${urms_native(resourceId)}\')</#if>', '2015-04-19 16:22:08', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-04-19 16:22:08', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '公共模块', '0');
INSERT INTO `sys_code_config` VALUES ('2fe1db9175ec450b904e76b62ab0eab2', '上报信访件', 'grab.visitcontents.upload.info', '', null, null, 'select * from VISIT_CONTENTS where \r\n resourceid in (select m.city_id from (select city_id from visit_handle_result where\r\nisread=0 AND flgDeleted=0 and RECORD_TYPE=\'upcityvisit\' limit  ${urms_original(counts)} ) \r\nas m)\r\n', '2015-10-13 14:21:38', 'system', '4028802f3fe01630013fe67349050002', '2015-10-13 14:21:38', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '对接接口', '0');
INSERT INTO `sys_code_config` VALUES ('32556694993d44f0b74b8ecda29080b3', '查询列表例子', 'um.sys.demo.queryDemoConfig', null, null, null, 'SELECT a.* from Sys_Asynchronous   a ,SYS_RigOrgRes  app \r\nwhere a.resourceId=app.appid AND  a.flgDeleted = 0   \r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if jobName??>  AND ( a.jobName= ${jobName})</#if>', '2015-03-04 17:24:33', 'system', '4028802f3fe01630013fe67349050002', '2015-03-04 17:24:33', 'system', '4028802f3fe01630013fe67349050002', '0', '0', null, '0');
INSERT INTO `sys_code_config` VALUES ('33080cf67d634c1cae896732d676a6e8', '信访人信息', 'um.visits.all.query_user', '', null, null, 'select  t.RESOURCEID,t.CREATETIME,t.CREATOR,t.CREATORID,t.UPDATETIME,t.UPDATER,t.UPDATERID,t.FLGDELETED,t.POLITICS,t.AGE,t.XM,t.XB,t.MZ,t.ZZ,\r\nsubstr(t.zzdm,0,6) AS ZZDM,t.ZJLX,t.ZJHM,t.SJH,t.HKSZD,t.TXDZ,t.GZDW,t.YZBM,t.GDDH,t.DZYJ,t.CSRQ,t.ZY,t.XH,t.XFJBH,t.VISIT_ID,t.ISREPORT \r\nFrom VISIT_USERS t where resourceid in(select appId from VISIT_LOGREPORT where exchangeType=\'1\' and isComplete=\'0\')', '2015-05-12 14:06:13', 'system', '4028802f3fe01630013fe67349050002', '2015-05-12 14:06:13', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('345aee56705c40f1b73c14e1b9d914d9', '6.18获取延时审批列表接口', 'um.visitdelayappylog.all.infolist', '', null, null, 'select * from  VISIT_DELAYAPPLYLOG where   isthrough<>2 AND   flgDeleted=0\r\nAND  resourceId in (<#if creatorId??> select appid from VISIT_CITY_LOGREPORT where city_ids=${creatorId} AND isComplete=0 AND flgDeleted=0</#if>\r\n<#if resourceIds??> AND visit_id=${urms_original(resourceIds)} </#if> )', '2015-08-12 10:52:43', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-08-12 10:52:43', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('34907ed92d9d4db7aae0a535dbc73f64', '意见提醒', 'um.visits.doopinion.query', null, null, null, 'from VisitDoopinion t where 1=1 \r\n<#if visitId??>  AND ( t.visit_id= ${visitId})</#if> \r\n<#if opiniontype??>  AND ( t.opiniontype= ${opiniontype})</#if> \r\norder by t.createTime desc', '2015-04-14 10:11:50', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-04-14 10:11:50', 'admin', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '1', '信访管理', '0');
INSERT INTO `sys_code_config` VALUES ('38305b10b851422c8408566bd4c1d290', '删除数据', 'um.sys.common.delete.obj', null, null, null, 'delete from ${urms_native(table)}   where  ${urms_native(param)}=${value}  ${urms_native(condition)}\n', '2014-11-05 12:15:55', 'system', '4028802f3fe01630013fe67349050002', '2014-11-05 12:15:55', 'system', '4028802f3fe01630013fe67349050002', '0', '1', '公共模块', '0');
INSERT INTO `sys_code_config` VALUES ('39b8b0e089ed4dc3add7d799dc0ae4cc', '梅州民声督办公示', 'um.visit.mzmsDbGs.query', '', null, null, 'select a.resourceId,a.gkxx,a.xm,date_format(a.xfrq,\'%Y-%m-%d\') xfrq,\r\ncase a.xfmddm when \'01\' then \'意见建议\' when \'02\' then \'申诉\' when \'03\' then \'求决\' when \'04\' then \'揭发控告\' else \'其他\' end xfmd,b.blbm,\r\nTIMESTAMPDIFF(DAY,DATE_FORMAT(b.createtime,\'%Y-%m-%d %H:%i:%s\'),b.expectTime) gzsx,\r\ncase when now()>b.expectTime then 0 else TIMESTAMPDIFF(DAY,DATE_FORMAT(NOW(),\'%Y-%m-%d %H:%i:%s\'),b.expectTime) end sysj \r\nfrom visit_contents a  left join (\r\nselect * from visit_handling where lamplevel=5) b\r\non a.resourceId=b.visit_id\r\nwhere a.xfxs=901 and a.PROSTATUS!=6 and a.publicType=1 \r\n<#if gkxx??>  and (a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if xfmddm??> AND a.xfmddm = ${xfmddm} </#if>\r\n<#if selectType==1>\r\n  and  a.xfrq>DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY)\r\n<#elseif selectType==2>\r\n  and  a.xfrq>DATE_ADD(curdate(),interval -day(curdate())+1 day)\r\n</#if>\r\norder by a.xfrq desc', '2015-11-30 18:58:36', 'system', '4028802f3fe01630013fe67349050002', '2015-11-30 18:58:36', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '梅州网站接口', '0');
INSERT INTO `sys_code_config` VALUES ('3baf43e488de41f0bd67b29920c0a188', '全部信访', 'um.visits.allvisit.all.query', '', null, null, 'select a.resourceid,a.BUSINESSNUM,a.visitnum,a.xfxs,a.XM,date_format(a.XFRQ,\'%Y-%m-%d\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,(case  when PROSTATUS =\'0\' then \'未处理\'\r\n      when PROSTATUS =\'1\' then \'审核中\' \r\n      when PROSTATUS =\'2\' then \'处理中\'\r\n      when PROSTATUS =\'3\' then \'退回申请\'\r\n      when PROSTATUS =\'4\' then \'延期申请\'\r\n      when PROSTATUS =\'5\' then \'申请办结\'\r\n      when PROSTATUS =\'6\' then \'已办结\' END)as prostatus,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.contentclassname,a.nrfldm,a.XFMDDM,a.SXR,a.XBJZSJ, \r\n lamp\r\nfrom VISIT_CONTENTS a where  1=1\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceId??> AND ( a.resourceId in (${urms_original(resourceId)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if contentclassname??>  AND ( a.contentclassname like \'%${urms_native(contentclassname)}%\')</#if>\r\n<#if nrfldm??>  AND ( a.nrfldm like \'${urms_native(nrfldm)}%\')</#if>\r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND ( a.processinguniidlike \'%${urms_native(processinguniid)}%\')</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if xfxs??>  AND ( a.xfxs=${xfxs})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND(  a.xfrq >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( a.xfrq<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if xfxs2??>  AND ( a.xfxs=${xfxs2})</#if>\r\n<#if visitLevel2??>  AND ( a.visitLevel=${visitLevel2})</#if>\r\n<#if startTime2??>  AND(  a.xfrq>= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( a.xfrq<= ${endTime2})</#if>\r\n<#if createorgid??>  AND  ( a.createorgid = ${createorgid})</#if>\r\n<#if createtime??>  AND ( a.createtime = \'${urms_native(createtime)}\')</#if> \r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n ORDER BY a.CREATETIME DESC', '2015-07-11 21:46:32', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-11 21:46:32', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '全部信访', '0');
INSERT INTO `sys_code_config` VALUES ('3f9ed612b6f34db0b22eecd3c260f629', '下级信访', 'um.visits.allvisit.downlevel.query', '', null, null, 'select a.resourceid,a.BUSINESSNUM,a.visitnum,a.xfxs,a.XM,date_format(a.XFRQ,\'yyyy-MM-dd\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,prostatus,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.CONTENTCLASSNAME,a.XFMDDM,a.SXR,a.XBJZSJ, \r\n lamp\r\nfrom VISIT_CONTENTS a where  a.VISITLEVEL>${visitLevel}  and SOURCETYPE=5 \r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND ( a.processinguniidlike \'%${urms_native(processinguniid)}%\')</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if xfxs??>  AND ( a.xfxs=${xfxs})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if xfxs2??>  AND ( a.xfxs=${xfxs2})</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime2})</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'yyyy-mm-dd hh24:mi:ss\') = \'${urms_native(createtime)}\')</#if>  \r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\nORDER BY a.CREATETIME DESC', '2015-07-13 18:21:36', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-13 18:21:36', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '全部信访', '0');
