INSERT INTO `sys_code_config` VALUES ('415d6237479e473198acebeff6574b12', '答复信息VISIT_REPLY', 'mz.visit_reply.all.infolist', '', null, null, 'select * from VISIT_REPLY where visit_id in(\r\nselect m.city_id from (select city_id from visit_handle_result where\r\nisread=0 AND flgDeleted=0 and RECORD_TYPE=\'upcityvisit\' limit  ${urms_original(counts)} ) \r\nas m) \r\n', '2015-12-07 09:27:04', 'system', '4028802f3fe01630013fe67349050002', '2015-12-07 09:27:04', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '数据对接', '0');
