INSERT INTO `sys_code_config` VALUES ('4291153a855244b0905920002e3c8302', '发生属地前10统计', 'web.problem.city.top10', '', null, null, 'select\r\n        *\r\nfrom\r\n        (\r\n                select\r\n                        count(city)as citycount,\r\n                        city as cityname\r\n                from\r\n                        (\r\n                              select case when citylevel =1\r\n                              then substr(substr(regionalsail,instr(regionalsail, \'/\')+1),1,instr(regionalsail, \'/\')-1)\r\n                              when citylevel=2 \r\n                              then substr(substr(substr(regionalsail,instr(regionalsail, \'/\')+1),instr(regionalsail, \'/\')+1),1,instr(regionalsail, \'/\')-1)\r\n                              when citylevel=3\r\n                              then\r\n                              substr(substr(substr(regionalsail,instr(regionalsail, \'/\')+1),instr(regionalsail, \'/\')+1),instr(regionalsail, \'/\')+1)\r\n                              end city\r\n                               from \r\n                              (\r\n                               select\r\n                                        t.wtsddm  ,\r\n                                        s.regnum  ,\r\n                                        s.regionalsail,\r\n                                        s.regionallevel,\r\n                                        (select regoldnum from petition_szlg_um.umregional where regnum=${registracode})prefix,\r\n                                        (select regionallevel from petition_szlg_um.umregional where regnum=${registracode})citylevel\r\n                                from\r\n                                        visit_contents t,\r\n                                        petition_szlg_um.umregional s\r\n                                where\r\n                                        t.wtsddm  =s.regnum\r\n                                    and t.wtsddm <>\'null\'\r\n                                    and datediff(now(),t.createtime)<30 and datediff(now(),t.createtime)>0\r\n                               )t\r\n                               where regnum like prefix||\'%\'\r\n                        )\r\n                        a\r\n                where\r\n                 a.city is not null\r\n                 and a.city <> \'\'\r\n                group by\r\n                        city\r\n                order by\r\n                        count(city) desc\r\n        )\r\n        b\r\nlimit 10', '2015-05-10 14:33:58', 'system', '4028802f3fe01630013fe67349050002', '2015-05-10 14:33:58', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '网站前端', '0');
INSERT INTO `sys_code_config` VALUES ('42c9d726d4c54191a66dbc9896040290', '组织架构模糊查询', 'um.umregional.vague', '', null, null, 'select if(o.resourceid is null,\'\',o.resourceid) as \"id\",if(o.levelregid is null,\'\',o.levelregid)  as pid,if(o.regname is null,\'\',o.regname)  as  “name”,replace(if(o.regionalsail is null,\'\',o.regionalsail),\'/\',\'\') as \"spare\", if(o.regnum is null,\'\',o.regnum) as \"code\",\'1\' as isparent from  petition_szlg_um.umregional o    where   1=1\r\n<#if spare??>  and  ( o.regionalsail  like \'%${urms_native(spare)}%\')  </#if> \r\n<#if regnum??>  and  ( o.regnum like \'${urms_native(regnum)}%\')  </#if> \r\norder by ordernum', '2015-09-02 13:59:08', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-09-02 13:59:08', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('42f069ac56064c14bd7301a32e47e0e9', '复核', 'um.visits.all.query_fh', '', null, null, 'SELECT T.fc_CHECK_FLAG, T.VISITNUM, T.RESOURCEID, R.BLJG, R.BLJGDM, T.GKXX, T.CREATETIME, T.FC_ACCEPTANCEDATE, \r\nT.FC_REPLYSITUATION, R.HANDLEUSEOPINION, R.SDFS, R.SDR, T.FC_SENDDATE, R.QSBZ, R.XFRYJ, T.FC_REVIEWATTACHNUM, \r\nT.FC_REVIEWREPLYATTACHNUM \r\nFROM VISIT_CONTENTS T, VISIT_REPLY R WHERE T.FC_APPLYTYPE = \'third\' AND T.VISITNUM = R.XFJBH AND T.RESOURCEID=R.VISIT_ID\r\nand t.RESOURCEID in(select appid from VISIT_LOGREPORT where exchangeType=\'6\' and isComplete=\'0\')\r\n\r\n', '2015-05-18 17:17:29', 'system', '4028802f3fe01630013fe67349050002', '2015-05-18 17:17:29', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '国家对接', '0');
