INSERT INTO `sys_code_config` VALUES ('4a3d968162174299be824fd2e7ec0278', '全部网信', 'um.visits.net.query', '', null, null, 'select 	a.resourceid,\r\n	a.businessnum,\r\n	a.gkxx,\r\n	a.visitnum,\r\n	a.xm,\r\n	date_format(a.XFRQ, \'%Y-%m-%d\') AS xfrq,\r\n	a.PROCESSINGUNIT,\r\n	a.curActivityName,\r\n	a.curOperator,\r\n	a.prostatus,\r\n	a.xfrs,\r\n	a.zz,\r\n	a.CONTENTCLASSNAME,\r\n	a.xfmddm,\r\n	a.netnumber,\r\n	a.callnumber,\r\n	a.reviewnumber,\r\n	a.supervisionnumber,\r\n	a.PROBLEMREGNAME,\r\n	a.letternumber,\r\n	a.receptionnumber,\r\n	a.djr,\r\n	a.lamp,\r\n	a.lamptext,\r\n	a.handletype\r\n from visit_contents   a\r\nwhere a.xfxs=\'300\' \r\nand WFSTATUS!=\'PRE\'\r\n<#if prostatus??>  AND ( TRIM(a.prostatus) = \'${urms_native(prostatus)}\')</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if visitnum2??>  AND (a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if createtime??>  AND ( a.createtime = \'${urms_native(createtime)}\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if startTime2??>  AND(  a.xfrq >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  (a.xfrq<= ${endTime2})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if xfmddm??>  AND ( a.xfmddm= ${xfmddm})</#if> \r\n<#if sjh??>  AND ( a.sjh= ${sjh})</#if> \r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if>\r\n<#if publicType??>  AND ( a.publicType= ${publicType})</#if> \r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if problemregname??>  AND ( a.problemregname like \'%${urms_native(problemregname )}%\')</#if>\r\n<#if contentclassname??>  AND ( a.contentclassname like  \'%${urms_native(contentclassname )}%\')</#if>\r\n<#if creator??>  AND ( a.creator like  \'%${urms_native(creator )}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like  \'%${urms_native(businessnum )}%\')</#if>\r\n<#if cxm??>  AND ( a.cxm like  \'%${urms_native(cxm )}%\')</#if>\r\n<#if zz??>  AND ( a.zz like  \'%${urms_native(zz)}%\')</#if>\r\n<#if xfmddm??>  AND ( a.xfmddm like  \'%${urms_native(xfmddm )}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit )}%\')</#if>\r\n<#if djr??>  AND ( a.djr like \'%${urms_native(djr)}%\')</#if>\r\n<#if publictype??>  AND ( a.publictype = \'${urms_native(publictype)}\')</#if>\r\n\r\n<#if visitLevel??>  AND ( visitLevel = \'${urms_native(visitLevel)}\')</#if>\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid =${createOrgId} )\r\n</#if>\r\n\r\norder by a.CREATETIME desc', '2015-03-28 16:25:46', '系统管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-03-28 16:25:46', '系统管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '网信管理', '0');
