INSERT INTO `sys_code_config` VALUES ('5c7e19512bb64222b10568c574374779', '本级来电', 'um.visit.call.query', '', null, null, 'select resourceid,businessnum,gkxx,visitnum,xm,date_format(a.XFRQ,\'%Y-%m-%d\') as xfrq,PROCESSINGUNIT,curActivityName,curOperator,(case when PROSTATUS=\'0\' then \'未处理\' when PROSTATUS=\'1\' then \'审核中\' when PROSTATUS=\'2\' then \'已处理\' when PROSTATUS=\'3\' then \'退回申请\' when PROSTATUS=\'4\' then \'审核中\' when PROSTATUS=\'5\' then \'申请办结\' when PROSTATUS=\'6\' then \'已办结\' end) as prostatus,xfrs, zz, CONTENTCLASSNAME, (case when xfmddm=\'01\' then \'意见建议\' when xfmddm=\'02\' then \'申诉\' when xfmddm=\'03\' then \'求决\' when xfmddm=\'04\' then \'揭发控告\' when xfmddm=\'99\' then \'其他\' end) as xfmddm,netnumber,callnumber, reviewnumber, supervisionnumber,PROBLEMREGNAME, letternumber, receptionnumber, lamp,lamptext,tsnr from visit_contents   a\r\nwhere a.xfxs=\'400\' \r\n\r\n<#if visitLevel??>  AND ( visitLevel = \'${urms_native(visitLevel)}\')</#if>\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n<#if prostatus??>  AND ( TRIM(a.prostatus) = \'${urms_native(prostatus)}\')</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if visitnum2??>  AND (a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'%Y-%m-%d\') = \'${urms_native(createtime)}\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'%Y-%m-%d\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'%Y-%m-%d\')<= ${endTime2})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if xfmddm??>  AND ( a.xfmddm= ${xfmddm})</#if> \r\n<#if sjh??>  AND ( a.sjh= ${sjh})</#if> \r\n<#if processinguniid??>  AND( ( a.processinguniid like \'%${urms_native(processinguniid)}%\') or  ( a.CREATORINDEXID like \'%${urms_native(processinguniid)}%\') )</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if>\r\n<#if publicType??>  AND ( a.publicType= ${publicType})</#if> \r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if wfstatus??>  AND ( a.wfstatus = ${wfstatus})</#if> \r\norder by a.CREATETIME desc', '2015-04-23 09:30:26', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-04-23 09:30:26', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '本级来电', '1');
