INSERT INTO `sys_code_config` VALUES ('6463c44858bf4715bf69a16f68890a9c', '上级信访', 'um.visits.allvisit.uplevel.query', '', null, null, 'select a.resourceid,a.BUSINESSNUM,a.visitnum,a.xfxs,a.XM,date_format(a.XFRQ,\'yyyy-MM-dd\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,OLDHANDLETYPE, prostatus, a.gkxx,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.CONTENTCLASSNAME,a.XFMDDM,a.SXR, \r\ndate_format(a.XBJZSJ,\'yyyy-MM-dd\') as XBJZSJ, \r\n lamp,lamptime\r\nfrom VISIT_CONTENTS a where  a.VISITLEVEL<${visitLevel} and SOURCETYPE=2\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND ( a.processinguniidlike \'%${urms_native(processinguniid)}%\')</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if xfxs??>  AND ( a.xfxs=${xfxs})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if xfxs2??>  AND ( a.xfxs=${xfxs2})</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime2})</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'yyyy-mm-dd hh24:mi:ss\') = \'${urms_native(createtime)}\')</#if> \r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n\r\n ORDER BY a.CREATETIME DESC', '2015-07-13 17:02:38', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-13 17:02:38', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '全部信访', '0');
