INSERT INTO `sys_code_config` VALUES ('70f32c1ec61f49af953fec33d94331ce', '首页红黄灯状态', 'um.visit.redyellowcount.index.query', '', null, null, 'select \'yellow_value\' name , count(1) value from visit_handling h where finishtime is null and creatorid =${operatorid} and (select lamp from  visit_contents where resourceId=h.visit_id )=2\r\nUNION ALL\r\nselect \'red_value\' name , count(1) value from visit_handling h where finishtime is null and creatorid =${operatorid} and (select lamp from  visit_contents where resourceId=h.visit_id )=3\r\nUNION ALL\r\nselect \'applyover_value\' name, count(1) value from visit_contents where CREATORINDEXID=${operatorid} and prostatus=5', '2015-07-01 19:54:42', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-01 19:54:42', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '系统首页', '0');
INSERT INTO `sys_code_config` VALUES ('72ed09b0fa9c469eadd09534d57df23b', '梅州民声回复列表', 'um.visit.mzmsReply.query', '', null, null, 'select b.resourceId,b.gkxx,b.xm,\r\ndate_format(b.xfrq,\'%Y-%m-%d\') xfrq,(satisfied+basicsatisfaction+dissatisfied) pfcs,\r\ncase b.xfmddm when \'01\' then \'意见建议\' when \'02\' then \'申诉\' when \'03\' then \'求决\' when \'04\' then \'揭发控告\' else \'其他\' end xfmd,\r\nb.processingunit bljg,date_format(a.blsj,\'%Y-%m-%d\') blsj,attention ydcs,case netType when 1 then \'书记信箱\' when 2 then \'市长信箱\' when 3  then \'局长接访\' when 4 then \'梅州民声\' when 5 then \'民声820\' when 6 then \'厓看民声\' when 7 then \'WAP\' when 8 then \'短信\' when 9 then \'电话信访\' when 10 then \'QQ信访\' else \'其他\' end netType\r\n  FROM  (\r\nselect visit_id,max(createtime) blsj from visit_reply group by visit_id\r\n) a left join visit_contents b on a.visit_id=b.resourceId\r\n where  b.PROSTATUS=6 and b.publicType=1\r\n<#if netTypeParameter==3>\r\nand b.xfxs=200 and b.lf_sleader=1\r\n<#elseif netTypeParameter==7 || netTypeParameter==8>\r\nand b.xfxs=300 and (b.netType=7 || b.netType=8) \r\n<#elseif netTypeParameter==9>\r\nand b.xfxs=400\r\n<#else>\r\nand b.xfxs=300 and b.netType=${netTypeParameter}\r\n</#if>\r\n<#if bljgmc??>  and b.processingunit=\'${bljgmc}\' </#if>\r\n<#if gkxx??>  and (b.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if xfmddm??> AND b.xfmddm = ${xfmddm} </#if>\r\n\r\n<#if selectType==1 || selectType==4>\r\n  and  a.blsj>DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY)\r\n<#elseif selectType==2  || selectType==5>\r\n  and  a.blsj>DATE_ADD(curdate(),interval -day(curdate())+1 day)\r\n</#if>\r\n<#if selectType==1 || selectType==2  || selectType==0>\r\n order by b.attention desc\r\n<#elseif selectType==3 || selectType==4  || selectType==5>\r\n order by (satisfied+basicsatisfaction+dissatisfied) desc\r\n<#else>\r\n order by a.blsj desc\r\n</#if>', '2015-11-30 15:57:38', 'system', '4028802f3fe01630013fe67349050002', '2015-11-30 15:57:38', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '梅州网站', '0');
INSERT INTO `sys_code_config` VALUES ('759023ee232e4619b0155d48bc55e271', '不予受理案件查询', 'um.visits.visitcontent.unaccpetinfo', '', null, null, 'SELECT\r\n        T.RESOURCEID,\r\n        T.VISITNUM,\r\n        T.GKXX   AS VISITTITLE   ,\r\n        SUBSTRING(T3.BLSJ,1,10)   AS REPLYDATE  ,\r\n        T.ATTENTION,\r\n        T.TSNR\r\nFROM\r\n        (SELECT V.RESOURCEID,\r\n       	V.ATTENTION,\r\n       	V.VISITNUM,\r\n       	V.GKXX,\r\n       	V.LAMP,\r\n       	V.TSNR,\r\n       	V.HANDLETYPE,\r\n       	V.PROSTATUS,\r\n       	V.XFMDDM,\r\n       	V.PUBLICTYPE,\r\n       	V.XFRQ,\r\n       	V.VISITLEVEL\r\n        FROM VISIT_CONTENTS V ) T\r\nLEFT JOIN\r\n        (                                                                 \r\n	select * from (select BLSJ,VISIT_ID  from VISIT_REPLY order by BLSJ desc) as a group by A.VISIT_ID\r\n        )\r\n        T3\r\nON\r\n        T3.VISIT_ID = T.RESOURCEID\r\nWHERE\r\n        T.HANDLETYPE LIKE \'0402%\'\r\nAND\r\n        T.PUBLICTYPE=\'1\'\r\nAND \r\n		T.XFMDDM<>\'04\'\r\nAND     \r\n		T.VISITLEVEL=3', '2015-05-20 14:15:15', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-05-20 14:15:15', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '网站前端', '0');
