INSERT INTO `sys_code_config` VALUES ('81d9d52e47b14019ba6c96023eb332cc', '查看详细', 'um.visits.webservice.queryDetails', '', null, null, 'SELECT  \r\n       VC.XM,\r\n        VC.RESOURCEID,\r\n        VC.GKXX  AS VISITTITLE           ,\r\n        VC.VISITNUM   AS VISITNUM      ,\r\n        VC.TSNR    AS VISITDETAIL ,\r\n        SUBSTRING(VC.XFRQ,1,10) AS SUBMITDATE,\r\n        VC.DJJGMC   AS DELIVERUNIT        ,\r\n        VC.PROCESSINGUNIT   AS  ACCPECTUNIT ,\r\n        case VC.xfmddm when \'01\' then \'意见建议\' when \'02\' then \'申诉\' when \'03\' then \'求决\' when \'04\' then \'揭发控告\' else \'其他\' end XFMD,\r\n        VE.DXFBMPJ AS UNITSATISFACTION         ,\r\n        VE.DXFBMPJNR AS DXFBMPJNR,\r\n        VE.DZRDWPJ   AS ENTITLEDSATISFACTION       , \r\n        VE.DZRDWPJNR AS DZRDWPJNR,\r\n        VE.PJZT  AS EVALUATESTATUS ,\r\n        VC.HANDLETYPE AS HANDLETYPE,\r\n        VC.PROSTATUS  AS PROSTATUS,\r\n        VC.PUBLICTYPE AS PUBLICTYPE,\r\n        VC.ISSATISFIED AS ISSATISFIED,\r\n        VC.SATISFIED  AS SATISFIED      ,\r\n        VC.BASICSATISFACTION  AS  BASICSATISFACTION,\r\n        VC.DISSATISFIED  AS  DISSATISFIED ,\r\n        VD.NOTICECONMENT \r\nFROM\r\n        VISIT_CONTENTS VC \r\n        LEFT JOIN\r\n        VISIT_EVALUATION VE \r\n        ON  VC.RESOURCEID = VE.VISIT_ID\r\n        LEFT JOIN\r\n        VISIT_DOOPINION VD\r\n        ON VD.RESOURCEID=VC.RESOURCEID\r\n        AND VD.OPINIONTYPE=2\r\nWHERE 1=1\r\n<#if resourceId??>  AND (VC.RESOURCEID =  \'${urms_native(resourceId)}\')</#if> \r\n', '2015-05-22 17:14:47', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-05-22 17:14:47', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '网站前端', '0');
INSERT INTO `sys_code_config` VALUES ('821e83b9e3db4da593e612fb83f1c96a', '满意度visitevaluation', 'mz.visitevaluation.all.infolist', '', null, null, 'select * from VISIT_EVALUATION  where visit_id in(\r\nselect m.city_id from (select city_id from visit_handle_result where\r\nisread=0 AND flgDeleted=0 and RECORD_TYPE=\'upcityvisit\' limit ${urms_original(counts)} ) \r\nas m) ', '2015-12-07 09:27:51', 'system', '4028802f3fe01630013fe67349050002', '2015-12-07 09:27:51', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '数据对接', '0');
INSERT INTO `sys_code_config` VALUES ('83ca9ae32a644521a183949a8786f9b5', '社会公众满意度（福田）', 'UM.VISIT.QUERY.DEPtNAME_SATISFACTION.PCT', '', null, null, 'WITH TEMPTABLE AS(\r\nSELECT  C.PROCESSINGUNIT,COUNT(E.DZRDWPJ) TOTAL,\r\nSUM(CASE E.DZRDWPJ WHEN 0 THEN 1 WHEN 1 THEN 1 ELSE 0 END) MANYI\r\n FROM VISIT_CONTENTS C LEFT JOIN VISIT_EVALUATION E ON C.RESOURCEID=E.VISIT_ID\r\n WHERE C.PROCESSINGUNIT IS NOT NULL\r\nGROUP BY C.PROCESSINGUNIT),\r\nTEMPRESULT AS(\r\n  SELECT ROWNUM SEQ,* FROM (\r\n   SELECT T.PROCESSINGUNIT,\r\n       (T.MANYI/T.TOTAL) PCT  \r\n  FROM TEMPTABLE T \r\n  WHERE T.TOTAL!=0 \r\n  ORDER BY (T.MANYI/T.TOTAL) DESC ,T.TOTAL)\r\n)\r\nSELECT T.PROCESSINGUNIT DEPTNAME,ROUND(T.PCT*100,2) RATE FROM TEMPRESULT T \r\n              WHERE T.SEQ<=3 \r\n                 OR T.SEQ>=(SELECT MAX(SEQ)-2 FROM TEMPRESULT)\r\n', '2015-06-27 14:36:38', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-27 14:36:38', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '网站前端', '0');
