INSERT INTO `sys_code_config` VALUES ('8e1b4fffc13f4b2ab877615eea8a26d7', '本级来信', 'um.visits.letter.query', '', null, null, 'select  a.resourceid,a.BUSINESSNUM,a.VISITNUM,a.XM,date_format(a.XFRQ,\'%Y-%m-%d\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,(case when PROSTATUS=\'0\' then \'未处理\' when PROSTATUS=\'1\' then \'审核中\' when PROSTATUS=\'2\' then \'处理中\' when PROSTATUS=\'3\' then \'退回申请\' when PROSTATUS=\'4\' then \'延期申请\' when PROSTATUS=\'5\' then \'申请办结\' when PROSTATUS=\'6\' then \'已办结\' end) as prostatus,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.CONTENTCLASSNAME,a.XFMDDM,a.SXR,date_format(a.XBJZSJ,\'%Y-%m-%d %H:%i:%s\') as XBJZSJ, \r\n lamp,lamptext,bjgccbz,check_flag,a.VISITTYPE\r\nfrom VISIT_CONTENTS a\r\nwhere  xfxs=\'100\'\r\n\r\n<#if visitLevel??>  AND (a.visitlevel= ${visitLevel})</#if>\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if qtcc??>  AND ( a.qtcc= ${qtcc})</#if> \r\n<#if bjgccbz??>  AND ( a.bjgccbz= ${bjgccbz})</#if> \r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND( ( a.processinguniid like \'%${urms_native(processinguniid)}%\') or  ( a.CREATORINDEXID like \'%${urms_native(processinguniid)}%\') )</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'%Y-%m-%d\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'%Y-%m-%d\')<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'%Y-%m-%d\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'%Y-%m-%d\')<= ${endTime2})</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'%m-%d-%Y %T:%i:%s\') = \'${urms_native(createtime)}\')</#if>  \r\n\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid =${createOrgId} )\r\n</#if>\r\nORDER BY a.CREATETIME DESC', '2015-04-07 10:37:45', 'system', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-03 22:48:21', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '本级来信', '0');
INSERT INTO `sys_code_config` VALUES ('8fa98c91a25c44f5b3581355f2e3e5e6', '上报信访件', 'um.common.reportrecord.update.visit.contents', '', null, null, 'select xfxs,t.* from visit_contents t where   t.check_flag<4 \r\nand  t.visitlevel<>0 \r\nand (t.xfxs=\'100\' or t.xfxs=\'200\' or t.xfxs=\'300\'or t.xfxs=\'301\'or t.xfxs=\'302\' \r\n	or t.xfxs=\'303\'or t.xfxs=\'400\'or t.xfxs=\'500\'or t.xfxs=\'600\'or t.xfxs=\'700\'or t.xfxs=\'999\')\r\nand (t.djjglb=\'0000\' or t.djjglb=\'0100\'or t.djjglb=\'0200\'or t.djjglb=\'0300\'or t.djjglb=\'1000\'\r\n	or t.djjglb=\'1100\'or t.djjglb=\'1200\'or t.djjglb=\'1300\'or t.djjglb=\'1400\')\r\nand t.xfjzt<\'16\'\r\nand t.bjgccbz<2 and t.qtcc<2 and cfxfbz<2\r\nand (t.xfmddm=\'01\' or t.xfmddm=\'02\' or t.xfmddm=\'03\' or t.xfmddm=\'04\' or t.xfmddm=\'99\')\r\nand (t.xfyydm<8 or  t.xfyydm=99 or t.xfyydm>0)\r\nand  t.sksflbz<2 and t.jabz<2 and t.fcbz<2 and t.fhbz<2\r\nand t.bjbz<2 and t.shrdbjbz<2 and t.lmbz<2 and t.gkbz<2\r\nand t.zdjjbz<2 and t.nmbz<2 and t.yybz<2\r\nand (t.ssxt<62 or t.ssxt=99 or t.ssxt>0)\r\nand (t.bfyrjb<9 or t.bfyrjb=99 or t.bfyrjb>0 or t.bfyrjb=\'\' or t.bfyrjb is null)\r\nand (t.rdwt!=0 or t.rdwt is null)\r\nand t.isreport=0 and t.resourceId=${resourceId}', '2015-06-02 09:39:28', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-02 09:39:28', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '国家对接', '0');
INSERT INTO `sys_code_config` VALUES ('906e979d86b748c4b5177904105774c2', '上报答复意见书信息', 'grab.visit_reply.all.infolist', '', null, null, 'select * from VISIT_REPLY    where\r\n visit_id in (\r\nselect city_id  from  visit_handle_result where\r\n  isread=0 AND flgDeleted=0 and  RECORD_TYPE=\'upcityvisit\'\r\n)', '2015-10-13 14:39:52', 'system', '4028802f3fe01630013fe67349050002', '2015-10-13 14:39:52', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '', '0');
INSERT INTO `sys_code_config` VALUES ('90801c7ad40e476cb58bd6e14309a191', '本级信访', 'um.visits.allvisit.thislevel.query', '', null, null, 'select a.resourceid,a.BUSINESSNUM,a.visitnum,a.xfxs,a.XM,date_format(a.XFRQ,\'yyyy-MM-dd\') as xfrq,a.PROCESSINGUNIT,a.BX_SMALLTO,a.HANDLETYPE,prostatus,a.tsnr,a.SJH,a.XFRS,a.ZJHM,a.ZZ,a.DJR,a.CONTENTCLASSNAME,a.XFMDDM,a.SXR,a.XBJZSJ, \r\n lamp,dc_isapply\r\nfrom VISIT_CONTENTS a where  a.VISITLEVEL=${visitLevel} and (SOURCETYPE=0 or SOURCETYPE=1 )\r\n<#if prostatus??>  AND (a.prostatus= ${prostatus})</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>  \r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if processingunit??>  AND ( a.processingunit like \'%${urms_native(processingunit)}%\')</#if>\r\n<#if processinguniid??>  AND ( a.processinguniidlike \'%${urms_native(processinguniid)}%\')</#if>\r\n<#if xfrs??>  AND ( a.xfrs=${xfrs})</#if>\r\n<#if visittype??>  AND ( a.visittype=${visittype})</#if>\r\n<#if xfxs??>  AND ( a.xfxs=${xfxs})</#if>\r\n<#if sxr??>  AND ( a.sxr=${sxr})</#if>\r\n<#if ssxt??>  AND ( a.ssxt=${ssxt})</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime})</#if>\r\n\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if businessnum2??>  AND ( a.businessnum like \'%${urms_native(businessnum2)}%\')</#if>\r\n<#if tsnr2??>  AND ( a.tsnr like \'%${urms_native(tsnr2)}%\')</#if>\r\n<#if zz2??>  AND ( a.zz like \'%${urms_native(zz2)}%\')</#if>\r\n<#if xfxs2??>  AND ( a.xfxs=${xfxs2})</#if>\r\n<#if startTime2??>  AND( date_format( a.xfrq,\'yyyy-mm-dd\') >= ${startTime2})</#if> \r\n<#if endTime2??>  AND  ( date_format( a.xfrq,\'yyyy-mm-dd\')<= ${endTime2})</#if>\r\n<#if createtime??>  AND ( date_format(a.createtime,\'yyyy-mm-dd hh24:mi:ss\') = \'${urms_native(createtime)}\')</#if> \r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n ORDER BY a.CREATETIME DESC', '2015-07-13 16:35:43', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-07-13 16:35:43', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '全部信访', '0');
INSERT INTO `sys_code_config` VALUES ('9089379b70a849988da04a671c9179dd', '判断信件是否存在', 'um.common.reportrecord.judge.visit.contents', '', null, null, 'select xfxs,t.* from visit_contents t where   t.check_flag<4 \r\nand  t.visitlevel<>0 \r\nand (t.xfxs=\'100\' or t.xfxs=\'200\' or t.xfxs=\'300\'or t.xfxs=\'301\'or t.xfxs=\'302\' \r\n	or t.xfxs=\'303\'or t.xfxs=\'400\'or t.xfxs=\'500\'or t.xfxs=\'600\'or t.xfxs=\'700\'or t.xfxs=\'999\')\r\nand (t.djjglb=\'0000\' or t.djjglb=\'0100\'or t.djjglb=\'0200\'or t.djjglb=\'0300\'or t.djjglb=\'1000\'\r\n	or t.djjglb=\'1100\'or t.djjglb=\'1200\'or t.djjglb=\'1300\'or t.djjglb=\'1400\')\r\nand t.xfjzt<\'16\'\r\nand t.bjgccbz<2 and t.qtcc<2 and cfxfbz<2\r\nand (t.xfmddm=\'01\' or t.xfmddm=\'02\' or t.xfmddm=\'03\' or t.xfmddm=\'04\' or t.xfmddm=\'99\')\r\nand (t.xfyydm<8 or  t.xfyydm=99 or t.xfyydm>0)\r\nand  t.sksflbz<2 and t.jabz<2 and t.fcbz<2 and t.fhbz<2\r\nand t.bjbz<2 and t.shrdbjbz<2 and t.lmbz<2 and t.gkbz<2\r\nand t.zdjjbz<2 and t.nmbz<2 and t.yybz<2\r\nand (t.ssxt<62 or t.ssxt=99 or t.ssxt>0)\r\nand (t.bfyrjb<9 or t.bfyrjb=99 or t.bfyrjb>0 or t.bfyrjb=\'\' or t.bfyrjb is null)\r\nand (t.rdwt!=0 or t.rdwt is null)\r\nand t.resourceId=${resourceId}', '2015-08-16 16:04:20', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-08-16 16:04:20', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '国家对接', '0');
INSERT INTO `sys_code_config` VALUES ('9126a48d43bb4b778abb9c9c577c8ea9', '附件表', 'um.attach.all.byappid.list', '', null, null, 'select * from SYS_ATTACHS where 1=1\r\n<#if resourceId??> AND appid in (select visitnum from VISIT_CONTENTS a where a.resourceId in (${urms_original(resourceId)})) </#if> \r\n<#if creatorId??>\r\nAND appid in ( select visit_num from VISIT_CITY_LOGREPORT where city_ids=${creatorId}\r\n AND isComplete=0 AND flgDeleted=0 )\r\nOR appid in (select resourceid from VISIT_DOOPINION where visit_id\r\nin (select visit_id from VISIT_CITY_LOGREPORT where city_ids=${creatorId} AND isComplete=0 AND flgDeleted=0 and visit_id <>\'\' ))\r\nOR appid in (select resourceid from VISIT_REPLY where XFJBH in\r\n(select visit_num from VISIT_CITY_LOGREPORT where city_ids=${creatorId}\r\nAND isComplete=0 AND flgDeleted=0 and visit_id <>\'\'))\r\n</#if>', '2015-08-12 18:49:33', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-08-12 18:49:33', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '', '1');
