INSERT INTO `sys_code_config` VALUES ('9160005ee69844a889f5e43100baaa01', '常用意见管理', 'um.configure.comopinionmanage.query', '', null, null, 'select a.* FROM VISIT_COMOPINION a where 1=1\r\n<#if areaid??>  AND ( a.areaid= ${areaid})</#if> \r\n<#if areaname??>  AND (a.areaname like \'%${urms_original(areaname)}%\')</#if> \r\n<#if visittype??>  AND ( a.visittype= ${visittype})</#if> \r\n<#if opintype??>  AND ( a.opintype= ${opintype})</#if> \r\norder by a.ordernum desc\r\n\r\n', '2015-03-28 15:20:24', 'system', '4028802f3fe01630013fe67349050002', '2015-03-28 15:20:24', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '信访配置', '0');
