INSERT INTO `sys_code_config` VALUES ('9a9a327d1e9f4a06a1c748732a55329c', '通用上级信件列表', 'um.visits.uplevelList.query', '', null, null, 'select distinct  a.resourceid,a.businessnum,a.tsnr,a.visitnum,a.xm,date_format(a.XFRQ,\'%Y-%m-%d\') as xfrq,a.PROCESSINGUNIT,a.HANDLETYPE,a.OLDHANDLETYPE,h.prostatus,a.lamp,a.lamptext,a.problemregname,a.sjh,a.xfrs,a.zjhm,a.zz,a.djr,a.creator,a.contentclassname,a.xfmddm,date_format(a.xbjzsj ,\'%Y-%m-%d\') xbjzsj\r\n  from visit_contents   a ,visit_handling h\r\nwhere  a.xfxs=${xfxs} \r\n       and h.visit_id=a.resourceid   \r\n<#if selectType?index_of(\'DB\')!=-1>\r\n   and a.checkstatus=0\r\n<#elseif selectType?index_of(\'ZS\')!=-1>\r\n   and a.checkstatus<>0 and a.prostatus in(1,2,3,4) and a.handletype=\'010000\'\r\n<#elseif selectType?index_of(\'JB\')!=-1>\r\n   and a.checkstatus<>0 and a.prostatus in(1,2,3,4) and a.handletype=\'020000\'\r\n<#elseif selectType?index_of(\'CGSB\')!=-1>\r\n   and a.prostatus=6\r\n<#elseif selectType?index_of(\'THS\')!=-1>\r\n   and a.prostatus=7\r\n<#elseif selectType?index_of(\'DSB\')!=-1>\r\n   and shrdbjbz=0 and prostatus=5\r\n<#elseif selectType?index_of(\'GJ\')!=-1>\r\n   and visitlevel=0\r\n<#elseif selectType?index_of(\'SJ\')!=-1>\r\n   and visitlevel=1\r\n<#elseif selectType?index_of(\'DSXJ\')!=-1>\r\n   and visitlevel=2\r\n<#elseif selectType?index_of(\'YBJ\')!=-1>\r\n   and a.prostatus=6\r\n</#if>\r\n<#if handlingLevel??> \r\n AND  a.visitlevel > ${handlingLevel} \r\n</#if>\r\n<#if handlingProstatus??> \r\n AND a.prostatus=${handlingProstatus} \r\n</#if>\r\n<#if visitLevel??> \r\n AND a.visitlevel<${visitLevel} \r\n</#if>\r\n<#if createOrgId??> \r\n AND h.qxjgdm=${createOrgId} \r\n</#if>\r\n\r\n\r\n<#if prostatus??>  AND (h.prostatus= ${prostatus})</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if> \r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if> \r\n<#if xfmddm??>  AND ( a.xfmddm=${xfmddm})</#if>\r\n<#if wtsddm??>  AND ( a.wtsddm=${wtsddm})</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>\r\n<#if problemregname??>  AND ( a.problemregname like \'%${urms_native(problemregname)}%\')</#if>\r\n<#if contentclassname??>  AND ( a.contentclassname like \'%${urms_native(contentclassname)}%\')</#if>\r\n<#if zz??>  AND ( a.zz like \'%${urms_native(zz)}%\')</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if businessnum??>  AND ( a.businessnum like \'%${urms_native(businessnum)}%\')</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if processinguniid??>  AND ( a.processinguniid= ${processinguniid})</#if> \r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if djr??>  AND ( a.djr like \'%${urms_native(djr)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if startTime??>  AND ( date_format(a.xfrq,\'%Y-%m-%d\') >= \'${urms_native(startTime)}\')</#if>  \r\n<#if endTime??>  AND ( date_format(a.xfrq,\'%Y-%m-%d\') <= \'${urms_native(endTime)}\')</#if>  \r\n<#if createtime??>  AND ( date_format(a.createtime,\'%Y-%m-%d %H:%i:%s\') = \'${urms_native(createtime)}\')</#if>  \r\norder by a.CREATETIME desc', '2015-05-12 12:29:15', '???', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-05-12 12:29:15', '???', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '上级信件', '0');
