INSERT INTO `sys_code_config` VALUES ('a609db7e967a438190b37c8cddd3d2aa', '办结率（福田）', 'UM.VISIT.QUERY.DEPTNAME_OVER.PCT', '', null, null, ' WITH TEMPTABLE AS(\r\n  SELECT T.PROCESSINGUNIT,\r\n         COUNT(1) TOTAL,\r\n         SUM(CASE T.PROSTATUS WHEN 6 THEN 1  ELSE 0 END) BANJIE \r\n    FROM VISIT_CONTENTS T \r\n   WHERE T.PROCESSINGUNIT IS NOT NULL\r\n   GROUP BY T.PROCESSINGUNIT\r\n    ),\r\n  TEMPRESULT AS(\r\n            SELECT ROWNUM SEQ,* FROM (\r\n                     SELECT T.PROCESSINGUNIT,\r\n                            (T.BANJIE/T.TOTAL) PCT \r\n                       FROM TEMPTABLE T \r\n                      ORDER BY (T.BANJIE/T.TOTAL) DESC, T.TOTAL ASC )\r\n  )\r\n    SELECT T.PROCESSINGUNIT DEPTNAME,ROUND(T.PCT*100,2) RATE FROM TEMPRESULT T \r\n              WHERE T.SEQ<=3\r\n                 OR T.SEQ>=(SELECT MAX(SEQ)-2 FROM TEMPRESULT)\r\n', '2015-06-27 14:37:15', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-06-27 14:37:15', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '网站前端', '0');
