INSERT INTO `sys_code_config` VALUES ('abba1ac82aff486b9be1573237b7cba7', '我的待办工作', 'mywork_visit', '', null, null, 'SELECT v.RESOURCEID,v.XFXS,v.VISITNUM,v.BUSINESSNUM,\r\n        v.XM,v.ZJHM,date_format(v.XFRQ,\'yyyy-mm-dd\') AS XFRQ,\r\n				v.PROSTATUS,v.HANDLETYPE,v.GKXX,\r\n        v.CONTENTCLASSNAME,v.DJR,v.ZZ,v.PROCESSINGUNIT\r\nFROM\r\n  VISIT_CONTENTS v\r\nWHERE\r\n1=1\r\n<#if xfxs??>  AND ( v.xfxs = \'${urms_native(xfxs)}\')</#if>\r\n<#if xm??>  AND ( v.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if zjhm??>  AND  ( v.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if gkxx??>  AND  ( v.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if handletype??>  AND  ( v.handletype =\'${urms_native(handletype)}\')</#if>\r\n<#if zz??>  AND ( v.zz = \'${urms_native(zz)}\')</#if>\r\n<#if sjh??>  AND ( v.sjh = \'${urms_native(sjh)}\')</#if>\r\n<#if prostatus??>  AND ( v.prostatus = \'${urms_native(prostatus)}\')</#if>\r\n<#if processingunit??>  AND ( v.processingunit= \'${urms_native(processingunit)}\')</#if>\r\n<#if resourceId??>  AND ( v.resourceId != \'${urms_native(resourceId)}\')</#if>', '2015-06-23 18:12:10', 'system', '4028802f3fe01630013fe67349050002', '2015-06-23 18:12:10', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '', '0');
