INSERT INTO `sys_code_config` VALUES ('af444f959915496591e8cca37b98fc20', '本级督查督办', 'um.visit.supervision.query.all', '', null, null, 'SELECT a.resourceid,a.VISITNUM,a.BUSINESSNUM,\r\n           a.publictype,a.XFXS,a.DC_SUPERVISESOURCE,\r\n             a.XM,DC_SOURCEMEMO,\r\n             date_format(a.XFRQ,\'%Y-%m-%d\') AS XFRQ, \r\n             a.CREATETIME AS CREATETIME, \r\n	   a.PROSTATUS as prostatus,a.HANDLETYPE,a.GKXX,\r\n        a.NRFLDM,a.XFRS,a.SJH,a.ZJHM,a.ZZ,a.DJR,a.PROCESSINGUNIT,a.CONTENTCLASSNAME,\r\n          lamp,lamptext\r\nFROM\r\n  VISIT_CONTENTS a\r\nWHERE\r\n  a.XFXS = \'901\'\r\n  and a.dc_isapply = 0\r\n<#if businessNum??>  AND ( TRIM(a.businessNum) = \'${urms_native(businessNum)}\')</#if>\r\n<#if businessNum2??>  AND ( TRIM(a.businessNum) = \'${urms_native(businessNum2)}\')</#if>\r\n<#if prostatus??>  AND ( TRIM(a.prostatus) = \'${urms_native(prostatus)}\')</#if>\r\n<#if visitnum??>  AND ( a.visitnum like \'%${urms_native(visitnum)}%\')</#if>\r\n<#if visitnum2??>  AND ( a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if gkxx2??>  AND ( a.gkxx like \'%${urms_native(gkxx2)}%\')</#if>\r\n<#if xm??>  AND ( a.xm like \'%${urms_native(xm)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if fc_applytype??>  AND ( TRIM(a.fc_applytype) = \'${urms_native(fc_applytype)}\')</#if>\r\n<#if fc_applytype2??>  AND ( TRIM(a.fc_applytype) = \'${urms_native(fc_applytype2)}\')</#if>\r\n<#if xfrq??>  AND ( a.xfrq = \'${urms_native(xfrq)}\')</#if>\r\n<#if createtime??>  AND ( a.createtime = \'${urms_native(createtime)}\')</#if>\r\n<#if handletype??>  AND ( TRIM(a.handletype) = \'${urms_native(handletype)}\')</#if>\r\n<#if publictype??>  AND ( TRIM(a.publictype) = \'${urms_native(publictype)}\')</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n\r\n<#if visitLevel??>  AND ( visitLevel = \'${urms_native(visitLevel)}\')</#if>\r\n<#if createOrgId??> \r\n AND (a.createOrgId= ${createOrgId} or a.processinguniid=${createOrgId} )\r\n</#if>\r\n\r\nORDER BY a.CREATETIME DESC', '2015-04-27 19:32:06', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '2015-04-27 19:32:06', '管理员', '4761EE3EA19CC50BE4D5A9C1A9BDA5AE', '0', '0', '督查督办', '0');
