INSERT INTO `sys_code_config` VALUES ('afc59e6b7fa24603803f8d216510d8ac', '办结案件查询', 'um.visits.visitcontent.completeinfo', '', null, null, 'SELECT\r\n        T.RESOURCEID AS RESOURCEID,\r\n        T.VISITNUM   AS VISITNUM  ,\r\n        T.GKXX       AS VISITTITLE,\r\n       SUBSTRING(T.XFRQ,1,10)       AS SUBMITDATE,\r\n        SUBSTRING(T3.BLSJ,1,10)      AS REPLYDATE ,\r\n        T.ATTENTION  AS ATTENTION,\r\n       T.TSNR\r\nFROM\r\n        (SELECT V.RESOURCEID,\r\n       	V.ATTENTION,\r\n       	V.VISITNUM,\r\n       	V.GKXX,\r\n       	V.LAMP,\r\n       	V.TSNR,\r\n       	V.HANDLETYPE,\r\n       	V.PROSTATUS,\r\n       	V.XFMDDM,\r\n       	V.PUBLICTYPE,\r\n       	V.XFRQ,\r\n       	V.VISITLEVEL\r\n        FROM VISIT_CONTENTS V ) T\r\nLEFT JOIN\r\n        (\r\n\r\n					select * from (select BLSJ,VISIT_ID  from VISIT_REPLY order by BLSJ desc) as a group by A.VISIT_ID             \r\n        )\r\n        T3\r\nON\r\n        T3.VISIT_ID = T.RESOURCEID\r\nWHERE\r\n        T.PROSTATUS = \'6\'\r\nAND\r\n        (T.HANDLETYPE=\'020000\' OR T.HANDLETYPE=\'010000\')\r\nAND\r\n        T.PUBLICTYPE=\'1\'\r\nAND\r\n        T.XFMDDM<>\'04\'\r\nAND     \r\n        T.VISITLEVEL=3\r\nORDER BY T.XFRQ DESC', '2015-04-19 16:56:45', 'system', '4028802f3fe01630013fe67349050002', '2015-04-19 16:56:45', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '网站前端', '0');
