INSERT INTO `sys_code_config` VALUES ('b037349f1fe34aa49d914bd35e3efc04', '复查', 'um.visits.all.query_fc', '', null, null, 'SELECT T.fc_CHECK_FLAG, T.VISITNUM, T.RESOURCEID, R.BLJG, R.BLJGDM, T.GKXX, T.CREATETIME, T.FC_ACCEPTANCEDATE, \r\nT.FC_REPLYSITUATION, R.HANDLEUSEOPINION, R.SDFS, R.SDR, T.FC_SENDDATE, R.QSBZ, R.XFRYJ, T.FC_REVIEWATTACHNUM, \r\nT.FC_REVIEWREPLYATTACHNUM \r\nFROM VISIT_CONTENTS T, VISIT_REPLY R WHERE T.FC_APPLYTYPE = \'second\' AND T.VISITNUM = R.XFJBH AND T.RESOURCEID=R.VISIT_ID\r\nand T.RESOURCEID in(select appid from VISIT_LOGREPORT where exchangeType=\'5\' and isComplete=\'0\')', '2015-05-18 17:04:11', 'system', '4028802f3fe01630013fe67349050002', '2015-05-18 17:04:11', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '国家对接', '0');
