INSERT INTO `sys_code_config` VALUES ('b66620a926ea4c448a9260ff1f7298bd', '所有下级信件', 'um.visits.lower.query', '', null, null, 'select a.resourceid,a.businessnum,a.gkxx,a.visitnum,a.xm,date_format(a.XFRQ,\'%Y-%m-%d\') as xfrq,a.processingunit,a.handletype,a.prostatus,a.tsnr,a.xfrs,a.contentclassname,\r\na.xfmddm,date_format(a.xbjzsj ,\'%Y-%m-%d\') as xbjzsj,\r\n a.lamp,a.lamptime,a.problemregname,a.sjh,a.zjhm, CONCAT(a.zz,\' \',txdz) as zz,\r\na.fc_acceptancedate ,a.isreport ,a.creator\r\nfrom \r\nVISIT_CONTENTS a , VISIT_HANDLING h\r\nwhere xfxs=\'200\' \r\nand a.resourceid=h.visit_id\r\nand h.prostatus = 6\r\n<#if visitLevel??>  \r\n     AND ( h.visitLevel = \'${urms_native(visitLevel+1)}\')\r\n</#if>\r\n<#if prostatus??>  AND ( a.prostatus = \'${urms_native(prostatus)}\')</#if>\r\n<#if resourceId??>  AND (  a.resourceId= ${resourceId})</#if>\r\n<#if resourceIds??> AND (  a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if handletype??>  AND (  a.handletype= ${handletype})</#if>\r\n<#if visitnum2??>  AND ( a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if xm2??>  AND (  a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if gkxx??>  AND (  a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if startTime??>  AND(  a.xfrq >= ${startTime})</#if> \r\n<#if endTime??>  AND  (  a.xfrq <= ${endTime})</#if>\r\n<#if tsnr??>  AND (  a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zjhm??>  AND (  a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if xfmddm??>  AND (  a.xfmddm= ${xfmddm})</#if> \r\n<#if sjh??>  AND (  a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if processinguniid??>  \r\nAND( ( a.processinguniid like \'%${urms_native(processinguniid)}%\') \r\nor  (  a.CREATORINDEXID like \'%${urms_native(processinguniid)}%\') )</#if>\r\n<#if publictype??>  AND (  a.publictype= ${publictype})</#if>\r\n<#if issatisfied??>  AND (  a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND (  a.cfxfbz= ${cfxfbz})</#if> \r\n<#if wfstatus??>  AND (  a.wfstatus != ${wfstatus})</#if>\r\norder by createTime desc', '2015-11-13 15:13:55', 'system', '4028802f3fe01630013fe67349050002', '2015-11-13 15:13:55', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '所有下级信件', '0');
INSERT INTO `sys_code_config` VALUES ('b6915c40cd10449d94c7b49d3c9c07be', '梅州Wap查询', 'um.visit.mzmsWapList.query', '', null, null, 'select resourceId,visitNum,cxm,gkxx,xm,case prostatus when 0 then \'未处理\' when 1  then \'审核中\' when 2 then \'处理中\' when 3 then \'退回申请\' when 4 then \'延期申请\' when 5 then \'申请办结\' when 6 then \'已办结\' \r\nelse \'其他\' end prostatus,\r\nprocessingunit from visit_contents where 1=1\r\n<#if visitNum?? && cxm??>\r\n and visitNum=${visitNum} and cxm=${cxm}\r\n<#else>\r\n and xfxs=300 and prostatus=6 and publicType=1\r\n</#if>\r\n', '2015-12-31 22:49:13', 'system', '4028802f3fe01630013fe67349050002', '2015-12-31 22:49:13', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '前端网站', '0');
