INSERT INTO `sys_code_config` VALUES ('b8747073cad74ec08428546a0a911a63', '新闻内容', 'web.news.content', '', null, null, 'select t.RESOURCEID,t.classname,t.classnum,t.ordernum,t.publicstatus,t.content,t.source,t.author,t.title,t.slide,t.publicdept,t.isslide,t.attachid,date_format(t.publictime,\'yyyy-mm-dd hh24:mi\') AS publictime\r\nfrom WEB_NEWS t where 1=1\r\n<#if author??>  AND (t.author like \'%${urms_original(author)}%\')</#if>\r\n<#if resourceId??> AND t.classnum in  (select b.classnum FROM WEB_COLUMN b WHERE b.levelclassid = ${resourceId}) or t.classnum = (select b.classnum FROM WEB_COLUMN b WHERE b.resourceId = ${resourceId}) </#if>  \r\n<#if classnum??>  AND  t.classnum in (${classnum})</#if> \r\n<#if classname??>  AND (t.classname like \'%${urms_original(classnamefen)}%\')</#if>\r\n<#if title??>  AND (t.title like \'%${urms_original(title)}%\')</#if>\r\n<#if publicstatus??>  AND t.publicstatus=${publicstatus}</#if> \r\n<#if isslide??> AND ( t.isslide= ${isslide}) AND t.slide is not null</#if>\r\norder by t.ordernum desc,t.publictime desc', '2015-03-31 16:04:58', 'system', '4028802f3fe01630013fe67349050002', '2015-03-31 16:04:58', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '网站内容管理', '0');
