INSERT INTO `sys_code_config` VALUES ('bb0025bedac34a74b7d6ce5a27c54ec5', '问题分类前10统计', 'web.problem.top10', '', null, null, 'select\r\n        *\r\nfrom\r\n        (\r\n                select\r\n                        t.classname as classname,\r\n                        count(t.classname)as classcount\r\n                from\r\n                        (\r\n                                select\r\n                                        substr(w.contentclassname, 0, instr(w.contentclassname, \'/\')-1) classname,\r\n                                        s.regnum,\r\n                                        (select regoldnum from petition_szlg_um.umregional where regnum=${registracode})prefix\r\n                                from\r\n                                        visit_contents w,\r\n                                        petition_szlg_um.umregional s\r\n                                where\r\n                                        w.wtsddm  =s.regnum\r\n                                    and datediff(now(),w.createtime)<30 and datediff(now(),w.createtime)>0\r\n                                    and w.contentclassname is not null\r\n                                   \r\n                        )\r\n                        t\r\n                where regnum like prefix||\'%\'\r\n                group by\r\n                        t.classname\r\n                order by\r\n                        count(t.classname) desc\r\n        )\r\n        a\r\nlimit 10', '2015-05-10 14:33:00', 'system', '4028802f3fe01630013fe67349050002', '2015-05-10 14:33:00', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '网站前端', '0');
