INSERT INTO `sys_code_config` VALUES ('bd145c4aa8304c2d87e1e5aee2e1471a', '梅州民声办理动态', 'um.visit.mzmsBldt.query', '', null, null, 'select bljg,date_format(max(blsj),\'%Y-%m-%d\') blsj,count(1) slzs,\r\ncount(case when finishtime is null  then 1 else null end) ygzzs,\r\ncount(case when finishtime is not null  then 1 else null end) ydfzs,\r\ncase when count(1)=0 then 0 else FORMAT((count(1)-count(case when lamp!=3 then 1 else null end))*100/count(1),2) end ascll\r\nfrom visit_handling where lamplevel=5\r\n<#if selectType==1>\r\n  and  blsj>DATE_ADD(CURDATE(),INTERVAL -WEEKDAY(CURDATE()) DAY)\r\n<#elseif selectType==2>\r\n  and  blsj>DATE_ADD(curdate(),interval -day(curdate())+1 day)\r\n</#if>\r\n group by bljg order by ascll desc', '2015-11-30 19:56:46', 'system', '4028802f3fe01630013fe67349050002', '2015-11-30 19:56:46', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '梅州网站', '0');
INSERT INTO `sys_code_config` VALUES ('bf94ee1055f745ed985baacf73f4b02d', '本级通用列表', 'um.visits.this.query', '', null, null, 'SELECT DISTINCT\r\n	a.resourceid,\r\n	a.businessnum,\r\n	a.gkxx,\r\n	a.visitnum,\r\n	a.xm,\r\n	date_format(a.XFRQ, \'%Y-%m-%d\') AS xfrq,\r\n	a.processingunit,\r\n	a.handletype,\r\n	a.prostatus,\r\n	a.tsnr,\r\n	a.xfrs,\r\n	a.contentclassname,\r\n	a.xfmddm,\r\n	date_format(a.xbjzsj, \'%Y-%m-%d\') AS xbjzsj,\r\n	IFNULL(a.lamp, \'0\') lamp,\r\n	a.lamptext,\r\n	a.problemregname,\r\n	a.sjh,\r\n	CAST(a.zjhm AS CHAR) zjhm,\r\n	CONCAT(a.zz, \' \', txdz) AS zz,\r\n	a.fc_acceptancedate,\r\n	a.isreport,\r\n	a.creator\r\nFROM\r\n	VISIT_CONTENTS a ,visit_handling h \r\nWHERE\r\n	1 = 1\r\nand a.RESOURCEID=h.VISIT_ID\r\n<#if xfxs??>  AND ( xfxs= \'${urms_native(xfxs)}\')</#if>\r\n<#if createOrgId??> \r\n AND (createOrgId= \'${urms_native(createOrgId)}\' or processinguniid=\'${urms_native(createOrgId)}\'\r\nor h.BLJGDM=\'${urms_native(createOrgId)}\' or h.QXJGDM=\'${urms_native(createOrgId)}\'\r\n )\r\n</#if>\r\n<#if checkstatus??>  AND ( a.checkstatus= ${urms_native(checkstatus)})</#if>\r\n<#if visitLevel??>  AND ( a.visitLevel = \'${urms_native(visitLevel)}\')</#if>\r\n<#if prostatus??>  AND ( a.prostatus in (${urms_native(prostatus)}))</#if>\r\n<#if resourceId??>  AND ( a.resourceId= ${resourceId})</#if>\r\n<#if resourceIds??> AND ( a.resourceId in (${urms_original(resourceIds)}) )</#if>\r\n<#if handletype??>  AND ( a.handletype= ${handletype})</#if>\r\n<#if visitnum2??>  AND (a.visitnum like \'%${urms_native(visitnum2)}%\')</#if>\r\n<#if xm2??>  AND ( a.xm like \'%${urms_native(xm2)}%\')</#if>\r\n<#if gkxx??>  AND ( a.gkxx like \'%${urms_native(gkxx)}%\')</#if>\r\n<#if startTime??>  AND( date_format( a.xfrq,\'%Y-%m-%d\') >= ${startTime})</#if> \r\n<#if endTime??>  AND  ( date_format( a.xfrq,\'%Y-%m-%d\')<= ${endTime})</#if>\r\n<#if tsnr??>  AND ( a.tsnr like \'%${urms_native(tsnr)}%\')</#if>\r\n<#if zjhm??>  AND ( a.zjhm like \'%${urms_native(zjhm)}%\')</#if>\r\n<#if xfmddm??>  AND ( a.xfmddm= ${xfmddm})</#if> \r\n<#if isreport??>  AND ( a.isreport= ${isreport})</#if> \r\n<#if sjh??>  AND ( a.sjh like \'%${urms_native(sjh)}%\')</#if>\r\n<#if processinguniid??>  AND( ( a.processinguniid like \'%${urms_native(processinguniid)}%\') or  ( CREATORINDEXID like \'%${urms_native(a.processinguniid)}%\') )</#if>\r\n<#if publictype??>  AND ( a.publictype= ${publictype})</#if>\r\n<#if issatisfied??>  AND ( a.issatisfied= ${issatisfied})</#if> \r\n<#if cfxfbz??>  AND ( a.cfxfbz= ${cfxfbz})</#if> \r\n<#if wfstatus??>  AND ( a.wfstatus = ${wfstatus})</#if>\r\n<#if lf_sleader??>  AND ( a.lf_sleader= ${lf_sleader})</#if>\r\norder by a.createTime desc', '2016-01-05 14:56:15', 'system', '4028802f3fe01630013fe67349050002', '2016-01-05 14:56:15', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '本级通用列表', '0');
