INSERT INTO `sys_code_config` VALUES ('d9447deb7d454367896509c046978d77', '分类判重计数', 'um.visit.visitcontents.query.repeat.count', '', null, null, 'select IFNULL(sum(case xfxs when \'100\' then 1 else 0 end),0) as letterNumber,\r\nIFNULL(sum(case xfxs when \'200\' then 1 else 0 end),0) as receptionNumber,\r\nIFNULL(sum(case xfxs when \'300\' then 1 else 0 end),0) as netNumber,\r\nIFNULL(sum(case xfxs when \'400\' then 1 else 0 end),0) as callNumber ,\r\nIFNULL(sum(case xfxs when \'901\' then 1 else 0 end),0) as supervisionNumber,\r\nIFNULL(sum(case xfxs when \'902\' then 1 else 0 end),0) as reviewNumber,\r\ncount(xfxs) as visitno\r\nfrom visit_contents v\r\nWHERE\r\n1=1\r\n<#if xm??>  \r\n   and v.resourceid in (\r\n		select visit_id from VISIT_USERS u where  \r\n			u.xm = ${xm}\r\n			<#if zz??> AND u.zz = ${zz}</#if>\r\n			<#if sjh??>  AND u.sjh = ${sjh}</#if>\r\n			<#if zjhm??> OR u.zjhm = ${zjhm}</#if>\r\n	)\r\n</#if>\r\n<#if resourceid??>  AND ( v.resourceid!= \'${urms_native(resourceid)}\')</#if>\r\n', '2015-11-28 11:59:54', 'system', '4028802f3fe01630013fe67349050002', '2015-11-28 11:59:54', 'system', '4028802f3fe01630013fe67349050002', '0', '0', '判重', '0');
